INSERT INTO `wp_posts` VALUES (748, 1, '2012-09-17 16:42:33', '2012-09-17 16:42:33', 'This was SPECIALLY designed to be used with our Graphic LCD Backpack.  Be sure you want to use this!  It is not only staggered on each line of header holes, but IT IS ALSO offset of the center point of the top and bottom lines by 5 mil.  This causes the headers to lock into place on the "standard" footprint on the LCD screen.  The extra squares on the tdocu layer are there simply to reference other pins (if you were to actually populate a longer header than ten long - this is what we do with the backpacks).', 'M10X2', '', 'publish', 'open', 'open', '', 'm10x2-2', '', '', '2012-09-17 16:42:33', '2012-09-17 16:42:33', '', 0, 'http://localhost/fz-parts/fz_part/m10x2-2/', 0, 'fz_part', '', 0); 
